mapper.select:
select *
from stores
where store_name = #{name}

mapper.update:
update stores set store_tel=#{store_tel}, store_address_si = #{store_address_si}, store_address_gu = #{store_address_gu}, store_address_dong = #{store_address_dong}, store_address_etc = #{store_address_etc}, store_desc=#{store_desc}, store_rnum=#{store_rnum},
store_saved_image=#{store_saved_image}, store_original_image=#{store_original_image},
opening_h_w_open=#{opening_h_w_open}, opening_h_w_close=#{opening_h_w_close}, opening_h_h_open=#{opening_h_h_open}, opening_h_h_close=#{opening_h_h_open}, holiday=#{holiday}, updated_at=sysdate where store_id = #{store_id}

mapper.select:
select cnt
from (select row_number() over (order by count(*) desc) as num,
             count(*)                                      cnt
      from likes
      group by store_id)
where num >= #{start}
  and num <= #{end}

select cnt
from (
         select num, cnt
         from (select comment_ref,
                      count(*) cnt
               from comments
               group by comment_ref) a
                  join
              (select row_number() over (order by count(*) desc) as num, store_id
               from likes
               group by store_id) b
              on a.comment_ref = b.store_id
     )
where num >= #{start}
  and num <= #{end}

select cnt
from (select row_number() over (order by count(*) desc) as num,
             count(*)                                      cnt
      from comments
      group by comment_ref)
where num >= #{start}
  and num <= #{end}
