mapper.select:
select user_id
from users
where user_email = #{user_email}

    	select * 
		from (
			select user_email, user_password, user_name
			, user_tel, user_address, user_profile, point, original_file
			from USERS
			left join USER_INFO
			on USERS.USER_ID = USER_INFO.USER_ID
			where USERS.user_id = #{id}
		    )

mapper.update:
update users
set user_password = #{user_password}
where user_id = #{user_id}
